  position: absolute;
  z-index: 0;

  &.left {
    width: 12%;
    bottom: 20%;
    left: 10%;
    animation: left-float 8s infinite alternate;
  }

  &.right {
    width: 15%;
    bottom: 60%;
    right: 10%;
    animation: right-float 8s infinite alternate;
  }

  @media (prefers-reduced-motion) {
    animation: none;
  }
}

@keyframes left-float {
  to {
    rotate: 5deg;
    transform: translateX(20px);
  }
}

@keyframes right-float {
  to {
    rotate: -20deg;
    transform: translateX(-20px);
  }
}

