  @media (min-width: 700px) {
    padding-inline: 6rem;
  }

  .icon {
    color: var(--color-accent);
    margin: 0 auto;
    font-size: 40px;
  }

  .quote {

    font-style: normal;
    font-weight: 500;
    font-size: clamp(1rem, 10vw, 2rem);
    line-height: 1.4;
  }

  img {
    border-radius: var(--border-radius);
    height: 100px;
    width: 100px;
    object-fit: cover;
    position: absolute;
    left: 50%;
    transform: translate(-50%, -50%);
    top: 100%;
  }

  :global(svg.graphic) {
    position: absolute;
    top: 0;
    right: 0;
  }
