@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");
@import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@300;400;500;600;700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Yeseva+One&display=swap');

hr {
    border-top-width: 4px;
    border-color: #194027;
  border-style: dotted;
}
:root {
  --verd-fosc: #194027;
  --verd-clar:  #388762;
  --taronja: #ff7f3f;
  --lila-fosc: #dac9fa;
  --lila: #e8e1fd;
  --verd-fluor: #f3f7a5;
  --crema: #fff5eb;

  --background: var(--crema);

  --color-accent: var(--verd-fosc);
}

#page {
  font-family: 'Space Grotesk', system-ui, sans-serif;
  color: var(--color-accent);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section-container {
  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  /* font-size: 3rem; */
  line-height: 1;
  font-weight: 700;
  margin: 0;
  font-family: 'Yeseva One';
}
h1.heading {
  font-size: 3rem;
}
h2.heading {
  font-size: 2.5rem;
}
h3.heading {
  font-size: 2rem;
}

.markdown h1, .markdown h2, .markdown h3 {
  font-family: 'Yeseva One';
}

.button {
  color: var(--crema);
  padding: 0.5em 1.1em;
  border-radius: 12px;

  &.primary.positive {
    background-color: var(--color-accent); 
    &:hover {
      background-color: var(--verd-clar);
    }
  }
  &.secondary.positive, &.tertiary.positive {
    color: var(--color-accent);
    &:hover {
      color: var(--verd-clar);
    }
  }
  &.secondary.positive {
    border: 1px solid var(--color-accent);
    &:hover {
      border-color: var(--verd-clar);
    }
  }

  &.primary.negative {
    background-color: var(--crema) !important;
    color: var(--color-accent);
    &:hover {
      background-color: var(--lila) !important;
    }
  }
  &.secondary.negative, &.tertiary.negative {
    &:hover {
      color: var(--lila);
    }
  }
  &.secondary.negative {
    border: 1px solid var(--crema);
    &:hover {
      border-color: var(--lila);
    }
  }
}

