@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

#page {
  font-family: system-ui, sans-serif;
  color: var(--color);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section-container {
  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

.section-container {} /* Defined in Site CSS */

.heading { /* Defined in Site CSS */
  text-align: center;
  margin-bottom: 3rem;
}

ul.cards {
  display: grid;
  place-items: center;
  gap: 2rem; 

  li {
    max-width: 400px;
    padding: 1.5rem;
    border: 1px solid var(--border-color, #eee);
    border-radius: var(--border-radius);

    figure {
      display: grid;
      place-items: center;


      img {
       padding:  2rem;
        aspect-ratio: 1 / 1;
        border-radius: 50%;
        object-fit: cover;

        width: 100%; /* For Safari */ 
      }
    }

    .info {

      .details {
        display: grid;
        gap: .25rem;
        text-align: center;
        margin-bottom: 1rem;

        span.name {
          font-weight: 600;
        }
        span.title{
          font-size: 1rem;
          color: #444;

        }
      }

      .social {
        display: flex;
        justify-content: center;
        gap: 1.25rem;
        font-size: 1.5rem;
        color: var(--color-accent, rebeccapurple);

        a {
          transition: var(--transition, 0.1s transform);

          &:hover {
            transform: scale(1.1);
          }
        }
      }
    }
  }

  @media (min-width: 600px) {
    grid-template-columns: 1fr 1fr;
  }

  @media (min-width: 1000px) {
    grid-template-columns: 1fr 1fr 1fr;
  }
}

.section-container { /* Defined in Site CSS */
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  padding-top: 1rem;
  padding-bottom: 1rem;

  @media (min-width: 600px) {
    flex-direction: row;
  }
}

nav {
  display: flex;
  gap: 1rem;

  .link { /* Defined in Site CSS */
    border-color: transparent;

    &:hover {
      border-color: initial;
    }
  }
}

.primo {
  font-weight: 600;
  font-size: 0.75rem;

  a {
    text-decoration: underline; 
    color: rgb(53, 217, 148);
  }
}

.social-links {
  display: flex;
  gap: 1.5rem;
  font-size: 1.5rem;

  a {
    color: var(--color-accent, rebeccapurple);
    transition: var(--transition, 0.1s transform);

    &:hover {
      transform: scale(1.1);
    }
  }
}

